//set a constant for declaring initial value of state
const colorReducer=(state='red',action)=> {
//Switch case for color changing
switch(action.type)
{
    case 'COLORRED':
        return state='red' ;

     case 'COLORBLUE':
     return state='blue';

     case 'COLORGREEN':
     return state='green';

     case 'COLOROrange':
     return state='orange';



}


}
